package com.example.menetrend;import static android.app.NotificationManager.IMPORTANCE_DEFAULT;import androidx.annotation.NonNull;import androidx.appcompat.app.AppCompatActivity;import androidx.core.app.ActivityCompat;import androidx.core.app.NotificationCompat;import androidx.core.app.NotificationManagerCompat;import android.Manifest;import android.app.Notification;import android.app.NotificationChannel;import android.app.NotificationManager;import android.content.Intent;import android.os.Bundle;import android.util.Log;import android.view.View;import android.view.animation.Animation;import android.view.animation.AnimationUtils;import android.widget.Button;import android.widget.EditText;import com.google.android.gms.tasks.OnCompleteListener;import com.google.android.gms.tasks.Task;import com.google.firebase.auth.AuthResult;import com.google.firebase.auth.FirebaseAuth;public class MainActivity extends AppCompatActivity {    private FirebaseAuth mAuth;    EditText emailET;    EditText passwdET;    private NotificationCompat.Builder NotificationChannel;    @Override    protected void onCreate(Bundle savedInstanceState) {        super.onCreate(savedInstanceState);        setContentView(R.layout.activity_main);        emailET = findViewById(R.id.et_email);        passwdET = findViewById(R.id.et_password);        mAuth = FirebaseAuth.getInstance();        android.app.NotificationChannel channel = new NotificationChannel("My notification","értesítés",IMPORTANCE_DEFAULT);        NotificationManager manager = getSystemService(NotificationManager.class);        manager.createNotificationChannel(channel);    }    @Override    protected void onStart() {        super.onStart();        ActivityCompat.requestPermissions(MainActivity.this,                new String[]{Manifest.permission.READ_EXTERNAL_STORAGE},                1);        Button btnlog = findViewById(R.id.btn_login);        Button btnreg = findViewById(R.id.btn_register);        Animation animation = AnimationUtils.loadAnimation(this, R.anim.slide_up);        btnlog.startAnimation(animation);        Animation animation2 = AnimationUtils.loadAnimation(this, R.anim.slide_row);        btnreg.startAnimation(animation2);    }    public void register(View view) {        Intent intent = new Intent(this,RegisterActivity.class);        startActivity(intent);    }    public void login(View view) {        Intent intent = new Intent(this,ListActivity.class);        String email = emailET.getText().toString();        String password = passwdET.getText().toString();        if(!email.isEmpty() && !password.isEmpty()) {            mAuth.signInWithEmailAndPassword(email, password).addOnCompleteListener(this, new OnCompleteListener<AuthResult>() {                @Override                public void onComplete(@NonNull Task<AuthResult> task) {                    if(task.isSuccessful())                    startActivity(intent);                }            });        }else{            Log.println(Log.ASSERT,"1","hiba!");        }    }}